# Vercel Environment Variables Setup Script
# Run this in PowerShell to set all AI-related env vars

$envVars = @{
    "GOOGLE_CLOUD_PROJECT" = "gen-lang-client-0578182636"
    "VERTEX_LOCATION" = "us-central1"
    "GOOGLE_SERVICE_ACCOUNT_KEY_BASE64" = ""
    "KIMI_API_KEY" = "sk-kimi-pGWtmFafZyKmUqYlxdK546z2KPua23csLWcJwoJEakZdVmEUvXammplpkowU8Sas"
    "ORACLE_API_KEY" = "dctgjfjkfgjrtj"
}

Write-Host "Setting Vercel Environment Variables..." -ForegroundColor Green
Write-Host ""

foreach ($var in $envVars.GetEnumerator()) {
    Write-Host "Adding $($var.Key)..." -ForegroundColor Yellow
    
    # Create temp file with value
    $tempFile = [System.IO.Path]::GetTempFileName()
    [System.IO.File]::WriteAllText($tempFile, $var.Value)
    
    # Add to Vercel
    vercel env add $($var.Key) production < $tempFile
    vercel env add $($var.Key) development < $tempFile
    vercel env add $($var.Key) preview < $tempFile
    
    # Cleanup
    Remove-Item $tempFile
    
    Write-Host "  âœ“ $($var.Key) added" -ForegroundColor Green
}

Write-Host ""
Write-Host "All environment variables set!" -ForegroundColor Green
Write-Host "Deploying to apply changes..." -ForegroundColor Yellow
vercel --prod
